package Lab.unit1;

public class DefaultValuedisplay {
	static int a ;
	static short a1;
	static long a2;
	static String str;
	static float b;
	static double b1;
	static boolean c;
	static byte x;
	static char ch;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("int Default Value:"+ a+"");
		System.out.println("short Default Value:"+ a1+"");
		System.out.println("long Default Value:"+ a2+"");
		System.out.println("String Default Value:"+ str+"");
		System.out.println("float Default Value:"+ b+"");
		System.out.println("double Default Value:"+ b1+"");
		System.out.println("boolean Default Value:"+ c+"");
		System.out.println("byte Default Value:"+ x+"");
		System.out.println("char Default Value:"+ ch+"");
		
		
	}

}
